-- Enabled Stroke Animation_G.WertUiAnimation = true-- Load Notificationlocal WertUi = loadstring(game:HttpGet("https://raw.githubusercontent.com/idcgj36/WertNotify/refs/heads/main/Obfuscate.lua"))()-- Notification 1WertUi:Notify({    Title = "First Notification",    -- Main heading    Description = "This is the First notification example.", -- Subtext    Duration = 4,    -- Display time in seconds    Corner = 8,    -- Background Corner    Transparent = 0.2,    -- Background transparency    Icon = "rbxassetid://76311199408449",    -- Icon ID    -- UiStroke (optional)    StrokeThickness = 2, -- Border thickness    StrokeColors = {          Color3.fromRGB(255, 0, 0),        Color3.fromRGB(255, 150, 0)        -- You can add more colors than that.    },    -- Buttons (optional)    Buttons = {        {            Text = "Yes",   -- Button text            Color = Color3.fromRGB(20, 255, 75),   -- Button color            Callback = function()                print("Yes")            end        },        {            Text = "No",            Color = Color3.fromRGB(255, 30, 30),            Callback = function()                print("No")            end        }        -- You can add multiple buttons.    }})wait(0.15)-- Notification 2WertUi:Notify({    Title = "Second Notification",    Description = "This is the Second notification example.",    Duration = 4,    Corner = 8,    Transparent = 0.2,    Icon = "rbxassetid://6034509993",    StrokeThickness = 2,    StrokeColors = {        Color3.fromRGB(255, 255, 255),         Color3.fromRGB(100, 100, 100)     },    Buttons = {        {            Text = "Accept",            Color = Color3.fromRGB(90, 140, 255),            Callback = function()                print("Accepted")            end        },        {            Text = "Decline",            Color = Color3.fromRGB(180, 90, 255),             Callback = function()                print("Declined")            end        }    }})